mapper.select:
		SELECT place.place_no AS no, title, gu_address, after_gu_address, detail_address, x, y, star, category, pet_type
		FROM place 
		LEFT OUTER JOIN (
			SELECT AVG(star) AS star, place_no 
			FROM reply 
			GROUP BY place_no
		) AS r ON place.place_no = r.place_no
		WHERE (title REGEXP #{title}
			OR gu_address REGEXP #{guAddress}
			OR after_gu_address REGEXP #{afterGuAddress}
			OR detail_address REGEXP #{detailAddress})

			AND category = #{category}


            AND (pet_type LIKE CONCAT('%', #{petType}, '%') OR pet_type IS NULL or pet_type = '기타')

SELECT no, title, gu_address, after_gu_address, detail_address, x, y, star, category, pet_type FROM ( SELECT p.place_no AS no, title, gu_address, after_gu_address, detail_address, x, y, star, category, pet_type FROM (SELECT * FROM place WHERE gu_address REGEXP #{guAddress}) AS p LEFT OUTER JOIN (SELECT place_no, AVG(star) AS star FROM reply GROUP BY place_no) AS r ON p.place_no = r.place_no ) AS outer_p WHERE (title REGEXP #{title} OR outer_p.after_gu_address REGEXP #{afterGuAddress} OR outer_p.detail_address REGEXP #{detailAddress})
AND outer_p.category = #{category}
AND (outer_p.pet_type LIKE CONCAT('%', #{petType} '%') OR outer_p.pet_type IS NULL or outer_p.pet_type = '기타')

mapper.insert:

INSERT INTO place (
	title, content, category, gu_address, after_gu_address, detail_address, x, y, user_id, pet_type
) VALUES (
	#{title}, #{content}, #{category}, #{guAddress}, #{afterGuAddress}, #{detailAddress}, #{x}, #{y}, #{userId}, pet_type
)
